class Solution {
public:
    int dp[2001][51] = {}, mod = 1000000007;
    int dfs(int i, int l1, int l2, vector<int>& nums) {
        if (i >= nums.size())
            return 1;
        if (dp[i][l1] == 0) {
            dp[i][l1] = 1;
            for (int n = l1; n <= nums[i]; ++n) {
                if (nums[i] - n <= l2)
                    dp[i][l1] = (dp[i][l1] + dfs(i + 1, n, nums[i] - n, nums)) % mod;
            }
        }
        return dp[i][l1] - 1;
    }
    int countOfPairs(vector<int>& nums) {
        return dfs(0, 0, 50, nums);
    }
};
