class Solution {
public:
int mod = 1000000007 ; 
int solve( int i , int p , vector<int>&a , vector<vector<int>>&dp){
    int n = a.size() ; 
  if(i == n)return 1 ; 
  if(dp[i][p+1] != -1)return dp[i][p+1] ; 

int ans = 0 ; 
  for(int j = max(0,p) ; j <= a[i] ; j++){
      if(p == -1){
      ans = (ans%mod + solve(i+1,j,a,dp)%mod)%mod ;
      }else{
         int p1 = a[i-1]-p ; 
         int p2 = a[i]-j ; 
         if(p2 <= p1){
            ans  = (ans%mod  + solve(i+1,j,a,dp)%mod)%mod ; 
         }
      }
  }

  return dp[i][p+1] = ans ; 


}
    int countOfPairs(vector<int>& nums) {
        int n = nums.size() ; 
         vector<vector<int>>dp(n , vector<int>(55,-1)) ; 
       return  solve(0,-1,nums,dp) ; 
    }
};
