class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        int n = nums.size();


        vector<vector<int>> dp(n, vector<int>(51, 0));


        for (int x = 0; x <= nums[0]; x++) {
            dp[0][x] = 1; 
        }


        for (int i = 1; i < n; i++) {
            for (int x = 0; x <= nums[i]; x++) {
                int y = nums[i] - x;
                if (y >= 0 && y <= 50) {
                    for (int k = 0; k <= x; k++) {

                        if (y <= nums[i-1] - k) {
                            dp[i][x] = (dp[i][x] + dp[i-1][k]) % MOD;
                        }
                    }
                }
            }
        }

        int result = 0;
        for (int x = 0; x <= 50; x++) {
            result = (result + dp[n-1][x]) % MOD;
        }

        return result;
    }
};
