int dp[2010][51];
class Solution {
public:
    int mod = 1e9+7;

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        int ans = 0;

        for(int i = 0; i <= nums[0]; i++) {
            int next = getans(1, i, nums);
            ans = ((1LL*ans)%mod + next)%mod;
        }
        return ans;
    }

    int getans(int curr, int lastVal, const vector<int> &arr) {
        if(curr == arr.size())
            return 1;
        if(dp[curr][lastVal] != -1)
            return dp[curr][lastVal];

        int remLast = arr[curr-1] - lastVal;

        int ans = 0;
        for(int i = lastVal; i <= arr[curr]; i++) {
            int j = arr[curr] - i;
            if(j >= 0 && j <= remLast) {
                ans = ((1LL*ans)%mod + getans(curr+1, i, arr)%mod)%mod;
            } 
        }
        dp[curr][lastVal] = ans;
        return ans;
    }
};
