class Solution {
public:
    int M = 1e9 + 7;
    map<pair<int,int>,int> mp;
    long long solve(int in,int prev,vector<int> &nums)
    {
        if(in >= nums.size())
        return 1;

        if(nums[in] < prev)
        return 0;

        pair<int,int> key = {in,prev};
        if(mp.find(key)!=mp.end())
        return mp[key];

        int lo = (in == 0 ? 0 : max(prev,nums[in] - (nums[in - 1] - prev)));
        int hi = nums[in];

        long long ans = 0;
        for(int i=lo;i<=hi;i++)
        {
            ans = (solve(in + 1,i,nums) + ans) % M;
        }

        return mp[key] = ans;
    }
    int countOfPairs(vector<int>& nums) 
    {
        return solve(0,0,nums);
    }
};
