class Solution {
    int n=-1;
    int mod=1000000007;
    int dp[2002][54];
    int util(int pos,int val, vector<int> & nums)
    {
        if(pos==n) return 1;
        //cout<<pos<<" ";
        if(dp[pos][val]!=-1) return dp[pos][val];
        int ans=0;
        //cout<<pos<<endl;
        //cout<<pos<<" "<<nums[pos]<<" "<<pos<<endl;
        int prev=nums[pos-1]-val;
       // cout<<prev<<" "<<nums[pos]-val<<" "<<pos<<" "<<val<<" "<<nums[pos]<<endl;
        for(int i=val;nums[pos]-i>=0;i++)
        {
            //cout<<nums[pos]-i<<" "<<pos<<endl;
            int t=nums[pos]-i;
            if(t>prev) continue;
            ans+=util(pos+1,i,nums);
            if(ans>=mod) ans%=mod;
        }
        return dp[pos][val]=ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        memset(dp,-1,sizeof(dp));
        int x=nums[0];
        int res=0;
        for(int i=0;i<=x;i++)
        {
            int temp=util(1,i,nums);
            //cout<<temp<<endl;
            res+=temp;
            if(res>=mod) res%=mod;
        }
        return res;
    }
};
