class Solution {
    int dp[2001][51][52];
    const int mod = 1e9 + 7;
    int n;
    int f(int i,int p1,int p2,vector<int>& nums){
        if(i == n)return 1;
        if(dp[i][p1][p2]!=-1)return dp[i][p1][p2];
        long ans = 0;
        for(int x=p1;x<=nums[i];++x){
            int j = nums[i]-x;
            if(j>=0 && j <= p2 && max(j,x)<=50){
                ans = (ans + f(i+1,x,j,nums))%mod;
            }
        }
        return dp[i][p1][p2] = ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        n = nums.size();
        for(int i=0;i<n;++i){
            for(int j=0;j<=50;++j){
                for(int k=0;k<=50;++k){
                    dp[i][j][k] = -1;
                }
            }
        }
        long ans = 0;
        for(int i=0;i<=nums[0];++i){
            if(nums[0]>=i && max(i,nums[0]-i)<=50)
            ans = (ans + f(1,i,nums[0]-i,nums))%mod;
        }
        return ans;
    }
};
