class Solution {
public:
    vector<vector<vector<int>>> dp;

    int solve(vector<int>& nums, int i, int prev1, int prev2)
    {
        if(i == nums.size()) return 1;
        if(dp[i][prev1][prev2] != -1) return dp[i][prev1][prev2];
        int ans = 0, M = 1000000007;
        for(int arr1 = 0; arr1 <= nums[i]; arr1++)
        {
            if(arr1 >= prev1 && nums[i]-arr1 <= prev2){
                ans = (ans + solve(nums, i+1, arr1, nums[i]-arr1))%M;
            }
        }
        return dp[i][prev1][prev2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        dp.resize(nums.size()+1, vector<vector<int>> (51, vector<int> (51,-1)));
        return solve(nums, 0, 0, 50);
    }
};
