class Solution {
public:
    int mod = 1e9+7;

    int solve(vector<int> &nums, int n, int i, int num, int pDec, vector<vector<vector<int>>> &dp){
        //base case
        if(i == n)
            return 1;

        if(dp[i][num][pDec] != -1)
            return dp[i][num][pDec];

        int cnt = 0;

        int val = nums[i];

        for(int v=num; v<=val; v++){
            if((val-v) <= pDec){
                cnt = ((cnt%mod) + (solve(nums, n, i+1, v, val-v, dp)%mod))%mod;
            }
        }

        return dp[i][num][pDec] = cnt;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        vector<vector<vector<int>>> dp(n+1, vector<vector<int>> (52, vector<int> (52, -1)));

        return solve(nums, n, 0, 0, 51, dp);
    }
};
