# https://leetcode.com/contest/weekly-contest-410/problems/find-the-count-of-monotonic-pairs-i/


from functools import cache
class Solution:
    def countOfPairs(self, nums: list[int]) -> int:
        n = len(nums)
        MOD = 10 ** 9 + 7

        @cache
        def func(i: int, umin: int, dmax: int):

            # print(i, umin, dmax)
            if i >= n:
                return 1

            ans = 0
            for x in range(max(umin, nums[i] - dmax), nums[i] + 1):
                ans += func(i + 1, x, nums[i] - x)
                ans %= MOD

            return ans

        return func(0, 0, max(nums))
