class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int N = nums.size();

        int dp[2001][51][51];
        memset(dp, -1, sizeof(dp));
        int mod = 1e9 + 7;

        function<int(int,int,int)> iter = [&](int i, int u, int v) {
            if(i>=N) { return 1; }

            auto& l = dp[i][u][v];
            if(l != -1) { return l; }

            int t = 0;

            for(int k=u; k<=nums[i]; ++k) {
                int remain = nums[i] - k;
                if(remain<=v) {
                    t = (t%mod + iter(i+1, k, remain)%mod)%mod;
                }
            }

            return l = t;
        };

        int t = 0;

        for(int k=0; k<=nums[0]; ++k) {
            t = (t%mod + iter(1, k, nums[0]-k) % mod)%mod;
        }

        return t;

        return iter(0, 0, 0);
    }
};
