#define ll long long int
#define mod 1000000007
class Solution {
public:

    ll f(int i,int p1,int p2,vector<int>&nums,vector<vector<vector<ll>>>&dp){

          if(i!=0 && (p1<0 || p2<0)) return 0;
          if(i>=nums.size()) return 1;

          if(dp[i][p1+1][p2+1]!=-1) return dp[i][p1+1][p2+1];
          int ans=0;

          for(int j=0;j<=nums[i];j++){
              if(p1==-1 &&  p2==-1){
                ans =( ans + f(i+1,j,nums[i]-j,nums,dp))%mod;
                //if(j!=nums[i]-j)ans = (ans + f(i+1,nums[i]-j,j,nums,dp))%mod;
              }else{
               if(p1<=j && p2>=nums[i]-j) ans = (ans +  f(i+1,j,nums[i]-j,nums,dp))%mod;
                //if( j!=nums[i]-j &&  p1<=nums[i]-j && p2>=j) ans = (ans + f(i+1,nums[i]-j,j,nums,dp))%mod;
              }
          }

          return dp[i][p1+1][p2+1]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {

        vector<vector<vector<ll>>>dp(nums.size()+2,vector<vector<ll>>(60,vector<ll>(60,-1)));
         return f(0,-1,-1,nums,dp);
    }
};
