class Solution {
public:
const int mod = 1e9 + 7;



    int countOfPairs(vector<int>& nums) {
  int n = nums.size();
    int mx = *max_element(nums.begin(), nums.end());


    vector<vector<int>> dp(mx + 1, vector<int>(mx + 1, 0));
    vector<vector<int>> suff(mx + 1, vector<int>(mx + 1, 0));


    for (int x = 0; x <= nums[0]; x++) {
        int y = nums[0] - x;
        if (y >= 0 && y <= mx) {
            dp[x][y] = 1;
        }
    }


    for (int i = 1; i < n; i++) {

        for (int prev_x = 0; prev_x <= mx; prev_x++) {
            suff[prev_x][mx] = dp[prev_x][mx];
            for (int prev_y = mx - 1; prev_y >= 0; prev_y--) {
                suff[prev_x][prev_y] = (suff[prev_x][prev_y + 1] + dp[prev_x][prev_y]) % mod;
            }
        }


        vector<vector<int>> new_dp(mx + 1, vector<int>(mx + 1, 0));
        for (int x = 0; x <= nums[i]; x++) {
            int y = nums[i] - x;
            if (y < 0 || y > mx) continue;


            for (int prev_x = 0; prev_x <= x; prev_x++) {
                new_dp[x][y] = (new_dp[x][y] + suff[prev_x][y]) % mod;
            }
        }
        dp = new_dp; 
    }


    int ans = 0;
    for (int x = 0; x <= mx; x++) {
        for (int y = 0; y <= mx; y++) {
            ans = (ans + dp[x][y]) % mod;
        }
    }

    return ans;
    }
};
