class Solution {
public:
    int dp[2010][55][55];
    long long mod = 1e9 + 7;
    long long f(int id,int p1,int p2,vector<int> &nums){
        if(id>=nums.size())return 1;
        if(dp[id][p1][p2]!=-1)return dp[id][p1][p2];
        long long ans =0;
        for(int i=0; i<=nums[id]; i++){
            int first = i;
            int second = nums[id]-i;
            if(first>=p1 and second<=p2){
                ans += f(id+1,first,second,nums);
                ans %= mod;
            }
        }
        return dp[id][p1][p2]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp,-1,sizeof(dp));
        return (int)f(0,0,51,nums);
    }
};
