class Solution {
public:
    int dp[2001][52][52];

    const int mod = 1e9 + 7;

    int f(int ind, int prev1, int prev2, vector<int> &nums) {
        if (ind == nums.size()) {
            return 1;
        }

        if (dp[ind][prev1][prev2] != -1) {
            return dp[ind][prev1][prev2];
        }

        int count = 0;
        for (int x = prev1; x <= nums[ind]; ++x) {
            int y = nums[ind] - x;
            if (y <= prev2) {
                count = (count + f(ind + 1, x, y, nums)) % mod;
            }
        }

        return dp[ind][prev1][prev2] = count;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int n=nums.size();

        return f(0, 0, 51, nums);        
    }
};
