class Solution {
public:
    int mod = 1e9+7;
    int dp[2000][51][51];
    int f(int index,int prevvalue1,int prevvalue2,vector<int>& nums,int n){
        if(index == n) return 1;
        if(dp[index][prevvalue1][prevvalue2]!=-1) return dp[index][prevvalue1][prevvalue2];
        int ans=0;
        int value = nums[index];
        if(index==0){
            for(int i=0;i<=nums[index];i++){
                ans  = (ans + f(index+1,i,nums[index]-i,nums,n))%mod;
            }
        }
        else{
            for(int i=0;i<=nums[index];i++){
                if(i >= prevvalue1 && (nums[index]-i)<=prevvalue2){
                    ans  = (ans + f(index+1,i,nums[index]-i,nums,n))%mod;
                }
            }
        }
        return dp[index][prevvalue1][prevvalue2]=ans;

    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp,-1,sizeof(dp));
        return f(0,1,1,nums,n);
    }
};
