class Solution {
public:

    bool isPalindrome(string s){
        int n = s.size();
        for(int i=0; i<n/2; i++){
            if(s[i] != s[n-i-1]){
                return false;
            }
        }
        return true;
    }

    bool isPrime(int n){
        for(int i=2; i*i<=n; i++){
            if(n % i == 0){
                return false;
            }
        }
        return true;
    } 

    ll checkAns(int idx, int idx1, int idx2, vector<int> &nums, vector<vector<vector<ll>>> &arr){
        if(idx >= nums.size()){
            return 1;
        }
        ll ans = 0;
        if(arr[idx][idx1][idx2] != -1){
            return arr[idx][idx1][idx2];
        }
        int val1 = idx1, val2 = nums[idx], val3 = idx2;
        for(int i=val1; i<=val2; i++){
            for(int j=val3; j>=0; j--){
                if(i + j != nums[idx]){
                    continue;
                }else{
                    ans += checkAns(idx+1, i, j, nums, arr);
                    ans = ans % MOD;
                }
            }
        }
        arr[idx][idx1][idx2] = ans;
        return ans;
    }

    int getAns(vector<int>& nums){
        int n = nums.size();
        vector<vector<vector<ll>>> arr(n, vector<vector<ll>> (51, vector<ll> (51)));
        for(int i=0; i<n; i++){
            for(int j=0; j<=50; j++){
                for(int k=0; k<=50; k++){
                    arr[i][j][k] = -1;
                }
            }
        }
        ll ans = checkAns(0, 0, nums[0], nums, arr);
        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        int ans = getAns(nums);
        return ans;
    }
};

