class Solution {
public:
int mod=1e9+7;
vector<vector<vector<int>>>dp;
long long int solve(int i,long long int p1,long long int p2,vector<int>&nums){
    int n=nums.size();
    if(i>=n)return 1;
    if(dp[i][p1][p2]!=-1)return dp[i][p1][p2];
    long long int ans=0;
    for(int j=0;j<=nums[i];j++){
        if(j>=p1 && ((nums[i]-j)<=p2)){
            ans+=solve(i+1,j,nums[i]-j,nums)*1ll;
            ans%=mod;
        }
    }
    return dp[i][p1][p2]=ans%mod;
}
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        dp.resize(n,vector<vector<int>>(51,vector<int>(51,-1)));
       return solve(0,0,50,nums); 
    }
};
