#define MOD 1000000007
class Solution {
public:

    int dp[51][2001];
    int dfs(vector<int>& nums, int prev1, int prev2, int ind)
    {
        if(ind >= nums.size())
        {
            return 1;
        }

        int ans = 0;
        if(dp[prev1][ind] != -1)
        {
            return dp[prev1][ind];
        }

        for(int i =0; i <= nums[ind]; i++ )
        {
            if(i >= prev1 && nums[ind] - i <= prev2)
            {
                ans += dfs(nums, i, nums[ind] - i, ind+1)%MOD;
                ans = ans%MOD;
            }
        }

        return dp[prev1][ind] = ans%MOD;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return dfs(nums, 0, 51, 0);

    }
};
