class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        ans = 0
        MOD = 10 ** 9 + 7
        mem = {}
        def helper(i, p1, p2):
            if i == n:
                return 1
            if (i,p1,p2) in mem:
                return mem[(i,p1,p2)]

            res = 0
            for candidate_1 in range(nums[i]+1):
                candidate_2 = nums[i] - candidate_1
                if p1 is None and p2 is None:
                    res += helper(i+1, candidate_1, candidate_2)
                else:
                    if p1 <= candidate_1 and p2 >= candidate_2:
                        res += helper(i+1, candidate_1, candidate_2)
            mem[(i,p1,p2)] = res % MOD
            return res % MOD
        return helper(0, None, None)
