class Solution {
public:
    int mod=1e9+7;
    int solve(vector<int>&nums,int ind,int p1,vector<vector<int>>&dp){
        int n=nums.size();
        if(ind>=n) return 1;
        if(dp[ind][p1+1]!=-1) return dp[ind][p1+1];
        int ans=0;
        int p2=p1==-1?-1: (nums[ind-1]-p1);
        for(int i=0;i<=nums[ind];i++){
            int u=i, v=nums[ind]-i;
            if(((p1==-1) && (p2==-1)) || ((u>=p1) && (v<=p2))){
                ans=(ans+ solve(nums,ind+1,u,dp)%mod)%mod;
            }
        }
        return dp[ind][p1+1]= ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>>dp(n+1,vector<int>(52,-1));
        return solve(nums,0,-1,dp);
    }
};
