class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10 ** 9 + 7
        mx = max(nums)

        @cache
        def dfs(x: int, low: int, up: int) -> int:
            if x == n:
                return 1
            target = nums[x]
            ans = 0
            for i in range(low, target+1):
                if target - i <= up:
                    ans = (ans + dfs(x+1, i, target - i)) % MOD
            return ans % MOD

        return dfs(0, 0, mx)
