class Solution {
    private static final int MOD = 1000000007;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int[][] dp = new int[n][51];

        for (int j = 0; j <= nums[0]; j++) {
            int k = nums[0] - j;
            dp[0][j] = 1; 
        }

        for (int i = 1; i < n; i++) {
            int[][] newDp = new int[n][51];
            for (int j = 0; j <= nums[i]; j++) {
                int k = nums[i] - j;
                for (int prev = 0; prev <= j; prev++) {  
                    if (nums[i-1] - prev >= k) { 
                        newDp[i][j] = (newDp[i][j] + dp[i-1][prev]) % MOD;
                    }
                }
            }
            dp = newDp;
        }

        int result = 0;
        for (int j = 0; j <= 50; j++) {
            result = (result + dp[n-1][j]) % MOD;
        }

        return result;
    }
}
