class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        MOD = 10 ** 9 + 7

        @cache
        def dp(idx, prev1, prev2):

            if idx == len(nums):
                return 1

            target = nums[idx]
            res = 0

            for arr1 in range(target+1):

                if arr1 < prev1 or target - arr1 > prev2 or target - arr1 < 0:
                    continue

                res += dp(idx+1, arr1, target - arr1)
                res %= MOD

            return res

        return dp(0, float('-inf'), float('inf'))
