class Solution {
public:
    int mod=1e9+7;
    int dp[2010][55];

    int rec(int idx,int prev, vector<int> &a, int n){
        if(idx==n) return 1;
        if(dp[idx][prev]!=-1) return dp[idx][prev];

        long long ans=0;

        if(prev==52){
            for(int i=0;i<=a[idx];i++){
                ans+=rec(idx+1,i,a,n);
                ans%=mod;
            }
        }
        else{
            for(int i=prev;i<=a[idx];i++){
                if(a[idx]-i > a[idx-1]-prev) continue;

                ans+= rec(idx+1,i,a,n);
                ans %=mod;
            }
        }
    ans %=mod;
    return dp[idx][prev]= ans;
    }
    int countOfPairs(vector<int>& a ) {
        int n=a.size();
        memset(dp,-1,sizeof(dp));

        return rec(0,52,a,n);
    }
};
