class Solution {
public:
    int mod = 1e9 + 7;
    int solve(vector<int> &nums, int idx, int pre_inc, int pre_dec, vector<vector<vector<int>>>& dp) {
        if (idx >= nums.size()) {
            return 1;
        }

        if (dp[idx][pre_inc][pre_dec] != -1) {
            return dp[idx][pre_inc][pre_dec];
        }

        long long ans = 0;
        for (int num = pre_inc; num <= nums[idx]; num++) {
            int second = nums[idx] - num;
            if (second <= pre_dec && second >= 0) {
                ans = (ans + solve(nums, idx + 1, num, second, dp)) % mod;
            }
        }

        dp[idx][pre_inc][pre_dec] = ans;
        return ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 0, 50, dp);
    }
};
