mod = 10 ** 9 + 7

class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = [1] * (nums[0] + 1)
        for v in nums[1:]:
            ndp = [0] * (v + 1)
            for i in range(len(dp)):
                for j in range(i, v + 1):
                    if len(dp) - 1 - i >= v - j:
                        ndp[j] += dp[i]
                        ndp[j] %= mod
            dp = ndp
        return sum(dp) % mod
