class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        constexpr int MOD = 1e9 + 7;
        constexpr int M = 1001;
        vector<int> dp(M, 1);
        int n = nums.size();

        for (int i = n; i --> 0;) {
            for (int p = 1; p < M; p++) {
                dp[p] = (dp[p] + dp[p-1]) % MOD;
            }
            vector<int> dp1(M, 0);
            for (int p = 0; p < M; p++) {
                int bound = nums[i];
                if (i > 0) {
                    int prev1 = nums[i-1] - p;
                    if (prev1 < 0) {
                        break;
                    }
                    bound -= prev1;
                }
                if (bound < 0) {
                    continue;
                }
                bound = min(bound, p);
                dp1[p] = dp[bound] % MOD;
            }
            dp = dp1;
        }

        return dp[M - 1];
    }
};
