class Solution {
public:
    int ans=0,mod=1e9+7;
    vector<vector<vector<int>>> dp;
    int calc(int i, int la, int lb, vector<int> &nums) {
        if(i==nums.size()) {
            return 1;
        }
        int c = 0;
        if(dp[i][la][lb]!=-1) return dp[i][la][lb];
        for (int j = la; j <= nums[i]; j++) {
            int temp = nums[i] - j;
            if (temp <= lb) { 
                c += calc(i + 1, j, temp, nums);
                c %= mod;
            }
        }
        return dp[i][la][lb]=c%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return calc(0,0,50,nums)%mod;
    }
};
