class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        n = len(nums)


        dp = [[0] * 51 for _ in range(n)]


        for x in range(nums[0] + 1):
            y = nums[0] - x
            dp[0][x] = 1


        for i in range(1, n):
            for x in range(51):
                y = nums[i] - x
                if 0 <= y <= 50:

                    for k in range(x + 1):
                        if y <= nums[i - 1] - k:
                            dp[i][x] = (dp[i][x] + dp[i - 1][k]) % MOD


        return sum(dp[n - 1]) % MOD
