class Solution {
public:
    int dp[2001][53];
    int mod=1e9+7;
    int helper(int i,vector<int>&nums,int fp){
        if(i==nums.size()) return 1;
        if(dp[i][fp+1]!=-1) return dp[i][fp+1];
        int ans=0;
        int sp=51;
        if(i>0) sp=nums[i-1]-fp;
        for(int x=0;x<=nums[i];x++){
            int f=x;
            int s=nums[i]-x;
            if(f>=fp && s<=sp){
                ans=(ans+helper(i+1,nums,f))%mod;
            }
        }
        return dp[i][fp+1]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return helper(0,nums,-1);
    }
};
