class Solution {
public:
    const int MOD = 1e9 + 7;
    vector<vector<vector<int>>> memo;
    vector<int> nums;

    int dp(int i, int j, int k) {
        if (i == 0) return 1;
        if (memo[i][j][k] != -1) return memo[i][j][k];

        int res = 0;
        for (int j1 = 0; j1 <= j; ++j1) {
            int k1 = nums[i-1] - j1;
            if (k1 >= k && k1 <= 50) {
                res = (res + dp(i-1, j1, k1)) % MOD;
            }
        }
        return memo[i][j][k] = res;
    }

    int countOfPairs(vector<int>& nums) {
        this->nums = nums;
        int n = nums.size();
        memo = vector<vector<vector<int>>>(n + 1, vector<vector<int>>(51, vector<int>(51, -1)));

        int res = 0;
        for (int j = 0; j <= 50; ++j) {
            int k = nums[n-1] - j;
            if (k >= 0 && k <= 50) {
                res = (res + dp(n-1, j, k)) % MOD;
            }
        }
        return res;
    }
};
