class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(),m=1e9+7;;
        long long res=0;;
        vector<vector<long long>> dp(n,vector<long long> (51,0));
        for(int i=0;i<n;i++){
            if(i==0){
                for(int j=0;j<=nums[i];j++){
                    dp[i][j]=1;
                }
            }
            else{
                for(int j=0;j<=nums[i];j++){
                    for(int k=0;k<=nums[i-1];k++){
                        if(j>=k && nums[i]-j<=nums[i-1]-k){
                            dp[i][j]+=dp[i-1][k];
                            dp[i][j]%=m;
                        }
                    }
                }
            }
            if(i==n-1){
                for(auto j: dp[i]){
                    res+=j;
                    res%=m;
                }
            }
        }
        return (int) res;
    }
};
