class Solution {
public:
    int dp[2001][52][52];
    int mod = 1e9+7;
    int find(int idx,int p1,int p2,vector<int>& nums){
        int n = nums.size();

        if(idx>=n) return 1;

        if(dp[idx][p1][p2]!=-1) return dp[idx][p1][p2];

        int ans = 0;

        int n1 = p1;
        while(n1<=nums[idx]){
            int n2 = nums[idx]-n1;
            if(n2<=p2) ans += find(idx+1,n1,n2,nums);
            ans = ans%mod;
            if(ans<0) ans += mod;

            n1++;
        }

        ans = ans%mod;
        if(ans<0) ans += mod;

        return dp[idx][p1][p2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        int ans = find(0,0,51,nums);
        return ans;
    }
};
