class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp = {}
        mod = 10**9 + 7

        def dfs(i, num1, num2):
            if i == len(nums):
                return 1
            if (i, num1, num2) in dp:
                return dp[(i, num1, num2)]

            dp[(i, num1, num2)] = 0
            for n in range(num1, nums[i] + 1):
                if nums[i] - n <= num2:
                    dp[(i, num1, num2)] += dfs(i + 1, n, nums[i] - n) % mod

            return dp[(i, num1, num2)] % mod

        return dfs(0, 0, 50) % mod
