class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        dp={}
        def solve(lprev,rprev,i):
            if(i<0):
                # print(lprev,rprev,i,'final')
                return 1
            if((lprev,rprev,i) in dp):
                return dp[(lprev,rprev,i)]
            res=0
            l,r=0,nums[i]
            while(l<lprev or r>rprev):
                l+=1
                r-=1
            # print(l,r,i)
            while(r>=0):
                res=(res+solve(l,r,i-1))%1000000007
                l+=1
                r-=1
                # print(l,r,i,'intermediate',res)
            dp[(lprev,rprev,i)]=res
            return res
        return solve(-1e9,1e9,len(nums)-1)
