class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long mod = 1e9+7;
        int n = nums.size();
        vector<vector<long long>>dp(n+1,vector<long long>(51,0));
        for(long long i=0;i<=nums[0];i++){
            long long val1 =  i;
            long long val2 = nums[0]-i;
            dp[0][val1] = 1;
        }
        for(int i=1;i<n;i++){
            for(int j=0;j<=nums[i];j++){
                long long val1 = j;
                long long val2 = nums[i]-j;
                for(long long k=0;k<=val1 && (nums[i-1]-k)>=val2;k++){
                    dp[i][j] = (dp[i][j]+dp[i-1][k])%mod;
                }
            }
        }
        long long ans = 0;
        for(int i=0;i<=50;i++){
            ans=(ans+dp[n-1][i])%mod;
        }
        return ans;
    }
};
