class Solution {
public:
    int dp[2001][51][51];
    int MOD=1e9+7;

    int help(int idx, int la, int lb, vector<int>& nums) {
        if (idx == nums.size()) {
            return 1;  // Reached the end, valid configuration
        }

        if(dp[idx][la][lb]!=-1) return dp[idx][la][lb];
        int res = 0;

        // Try all possible values for arr1[i]
        for (int x = la; x <= nums[idx]; x++) {
            int y = nums[idx] - x;
            if (y <= lb) {
                res = (res + help(idx + 1, x, y, nums)) % MOD;
            }
        }

        return dp[idx][la][lb] = res;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return help(0, 0, nums[0], nums);
    }
};
