class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        # when nums[i] - nums[i-1] > 0, the non-increasing array should decrease by at least nums[i] - nums[i-1]
        mod = 10**9 + 7
        n = len(nums)
        @cache
        def dfs(p, last):
            ans = 0
            if p == n or last < 0:
                return 1
            if p == 0 or nums[p] >= nums[p-1]:
                for i in range(last + 1):
                    ans += dfs(p+1, i)
                return ans
            else:
                for i in range(last - (nums[p-1] - nums[p]) + 1):
                    ans += dfs(p+1, i)
                return ans

        return dfs(0, nums[0]) % mod
