class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int MOD = 1e9 + 7;

        vector<vector<vector<int>>> dp(n, vector<vector<int>>(51, vector<int>(51, -1)));

        function<int(int, int, int)> recur = [&](int ind, int a1, int a2) {
            if (ind == n) return 1;
            if (dp[ind][a1][a2] != -1) return dp[ind][a1][a2];

            int ways = 0;
            for (int i = 0; i <= nums[ind]; ++i) {
                if (nums[ind] - i >= a1 && i <= a2) {
                    ways = (ways + recur(ind + 1, nums[ind] - i, i)) % MOD;
                }
            }
            return dp[ind][a1][a2] = ways;
        };

        return recur(0, 0, nums[0]) % MOD;
    }
};
