class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7
        # print("----")
        @lru_cache(None)
        def dp(i, inc,dec):
            # print(arr)
            if i == n:
                return 1
            if nums[i] < inc:
                return 0
            ret = 0
            for val in range(inc, nums[i] + 1):

                rem = nums[i] - val
                if rem <= dec:
                    # arr.append(val)
                    ret += dp(i + 1, val, rem)
                    # arr.pop()
                ret %= MOD

            return ret

        return dp(0, 0,inf)
