class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7
        @cache
        def dp(i, val):
            if i == (len(nums)-1):
                return 1
            val1 = val
            val2 = nums[i] - val1
            res = 0
            for new_val1 in range(val1, nums[i+1]+1):
                new_val2 = nums[i+1] - new_val1
                if new_val2 > val2:
                    continue
                res += dp(i+1, new_val1)
            return res


        res = 0
        for i in range(nums[0]+1):
            res += dp(0, i) % MOD
        return res % MOD
