class Solution {
public:
    const int MOD=1e9+7;
    int dp[2001][52][52];
    int f(int ind, vector<int>& nums, int prev1, int prev2) {
        int n = nums.size();
        if (ind == n) return 1;
        if (dp[ind][prev1 + 1][prev2] != -1)return dp[ind][prev1 + 1][prev2];
        int ans = 0;       
        for (int i = 0; i <= nums[ind]; i++){
            int arr1_val = i;
            int arr2_val = nums[ind] - i;
            if (arr1_val >= prev1 && arr2_val <= prev2) {
                ans = (ans + f(ind + 1, nums, arr1_val, arr2_val)) % MOD;
            }
        }   
        return dp[ind][prev1 + 1][prev2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof dp);
        return f(0, nums, -1, 50);
    }
};
