class Solution {

    int MOD = (int)1e9 + 7;
    public int countOfPairs(int[] nums) {

        int n = nums.length;
        Integer[][] memo = new Integer[n][51];
        return recursion(nums, 0, 0, 50, memo);
    }

    private int recursion(int[] nums, int index, int val1, int val2, Integer[][] memo){

        if(index == nums.length){
            return 1;
        }

        if(memo[index][val1] != null){
            return memo[index][val1];
        }

        int result = 0;
        for(int i = val1; i <= nums[index]; i++){
            int j = nums[index] - i;

            if(i < val1 || j > val2){
                continue;
            }
            result = (result + recursion(nums, index + 1, i, j, memo)) % MOD;
        }
        memo[index][val1] = result;
        return result;
    }
}
