class Solution {
public:
    vector<int> v;
    long long dp[2001][52][52];
    int mod = 1e9 + 7;
    long long rec(int x,int p,int q){
        int n = v.size();
        if(x >= n) return 1;
        if(dp[x][p+1][q] != -1) return dp[x][p+1][q];
        long long l = 0,r = v[x],res = 0;
        while(l <= v[x]){
            if(p <= l and q >= r){
                res += rec(x+1,l,r);
            }
            l++;r--;
        }
        return dp[x][p+1][q] = res % mod;
    }
    int countOfPairs(vector<int>& nums){
        v = nums;
        memset(dp,-1,sizeof(dp));
        return rec(0,-1,51);
    }
};
