const int N = 2e3 + 3;
const int MOD = 1e9 + 7;

class Solution {
public:
    int dp[N][55], a[N];
    int countOfPairs(vector<int>& nums) {

        int n = (int)nums.size();
        for(int i = 0; i < (int)nums.size(); i++)
            a[i + 1] = nums[i];

        for(int i = 0; i <= a[1]; i++)
            dp[1][i] = 1;

        for(int i = 2; i <= n; i++)
        {
            for(int cur = 0; cur <= a[i]; cur++)
            {
                for(int prev = 0; prev <= min(cur, a[i - 1]); prev++)
                {
                    int prev2 = a[i - 1] - prev;
                    int cur2 = a[i] - cur;
                    if(cur2 <= prev2)
                    {
                        (dp[i][cur] += dp[i - 1][prev]) %= MOD;
                    }
                }
            }
        }
        int ans = 0;
        for(int i = 0; i <= a[n]; i++)
            (ans += dp[n][i]) %= MOD;
        return ans;
    }   
};
