class Solution {
public:
    #define ll long long int
    const int mod=1e9+7;
    ll solve(int idx, int prev, vector<int>& nums, vector<vector<ll>> &dp){
        if(idx==nums.size()){
            return 1;
        }

        if(dp[idx][prev]!=-1){
            return dp[idx][prev];
        }

        ll ans=0;
        if(prev==60){
            for(int i=0;i<=nums[idx];i++){
                if(nums[idx]-i>=0){
                    ans=(ans+solve(idx+1,i,nums,dp))%mod;
                }
            }
        }else{
            for(int i=0;i<=nums[idx];i++){
                if(nums[idx]-i>=0 && i>=prev && nums[idx]-i<=nums[idx-1]-prev){
                   ans=(ans+solve(idx+1,i,nums,dp))%mod;
                }
            }
        }
        return dp[idx][prev]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<ll>> dp(n+1,vector<ll>(100,-1));
        return solve(0,60,nums,dp);
    }
};
