class Solution {
public:
    int n;
    int mod=1e9+7;
    // i=0 ---  p1=0-------p2=50
    int dp[2001][100];
    int fun(int i,int p1,vector<int> &nums){
        if(i==n) return 1;

        int &ans=dp[i][p1];
        if(ans!=-1) return ans;

        int p2=1e9;
        if(i-1>=0) p2= nums[i-1]-p1;


        int sum=0;
        for(int c1=p1;c1<=nums[i];c1++){
            int c2=nums[i]-c1;
            if(p1<=c1 && p2>=c2){
                sum=(sum+fun(i+1,c1,nums))%mod;
            }

        }

        return ans= sum;
    }
    int countOfPairs(vector<int>& nums) {
        n=nums.size();
        memset(dp,-1,sizeof(dp));
        return  fun(0,0,nums);
    }
};
