class Solution {
    long mod=(long)(1e9+7l);
    public int countOfPairs(int[] nums) {
        int n=nums.length;
        long dp[][][]=new long[52][52][n+1];
        for(int i=0;i<=51;i++)
            for(int j=0;j<=51;j++)Arrays.fill(dp[i][j],-1l);
        return (int)solve(0,0,50,nums,dp);
    }
    public long solve(int curr,int prev1,int prev2,int nums[],long dp[][][]){
        if(curr>=nums.length)return 1;
        if(dp[prev1][prev2][curr]!=-1)return dp[prev1][prev2][curr];
        int num=nums[curr]; long count=0;
        for(int i=prev1;i<=num;i++){
            if(prev2>=num-i){
                //System.out.println(curr+" "+num+" "+i+" "+(num-i));
                count=(count%mod+solve(curr+1,i,num-i,nums,dp)%mod)%mod;
            }
        }
        return dp[prev1][prev2][curr]=count;
    }
}
