class Solution {
public:
    long long dp[2001][52][52];
    int mod=1e9+7;
    int solve(vector<int>&arr,int index,int prev1,int prev2)
    {
        if(index >=arr.size())return 1;
        if(dp[index][prev1+1][prev2+1]!=-1)return dp[index][prev1+1][prev2+1];
        int val=arr[index];
        long long ans=0;
        for(int i=0;i<=val;i++)
        {
            if((prev1==-1 and prev2==-1) or prev1 <= i and prev2 >= val-i)
               ans= (ans%mod + solve(arr,index+1,i,val-i)%mod)%mod;
        }

        return dp[index][prev1+1][prev2+1]=ans%mod;

    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return solve(nums,0,-1,-1);
    }
};
